import React, { useEffect, useState } from 'react'

function encrypt(message, keyword) {
    
    message = message.replace(/[^a-zA-Z]/g, '').toUpperCase();
    keyword = keyword.replace(/[^a-zA-Z]/g, '').toUpperCase();
    
    let encryptedMessage = '';
    let keywordIndex = 0;

    for (let i = 0; i < message.length; i++) {
        const messageChar = message.charCodeAt(i) - 65;
        const keywordChar = keyword.charCodeAt(keywordIndex % keyword.length) - 65;

        const encryptedChar = String.fromCharCode((messageChar + keywordChar) % 26 + 65);
        encryptedMessage += encryptedChar;

        keywordIndex++;
    }

    return encryptedMessage;
}


const Home = () => {

    useEffect(() => {

    })

    const [password, setPassword] = useState('');
    const [key, setKey] = useState('');
    const [output, setOutput] = useState('');

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };

    const handleKeyChange = (e) => {
        setKey(e.target.value);
    };

    const handleFormSubmit = (e) => {
        e.preventDefault();
        
        const encryptedMessage = encrypt(password, key)
        setOutput(`Encrypted Message: ${encryptedMessage}`);
    };

    return (
        <div className="home">
            <form onSubmit={handleFormSubmit}>
                <div>
                    <label htmlFor="password">Password:</label>
                    <input 
                        type="password" 
                        id="password" 
                        value={password} 
                        onChange={handlePasswordChange} 
                    />
                </div>
                <div>
                    <label htmlFor="key">Key:</label>
                    <input 
                        type="text" 
                        id="key" 
                        value={key} 
                        onChange={handleKeyChange} 
                    />
                </div>
                <button type="submit">Submit</button>
            </form>
            <section className="output">
                <h3>Output:</h3>
                <p>{output}</p>
            </section>
        </div>
    );
}

export default Home
