import React, { useEffect, useState } from "react";


function encrypt(message, keyword) {

    let encryptedMessage = "";

    for (let i = 0; i < message.length; i++) {

        let messageLetterNum = message.charCodeAt(i) - 65
        console.log(messageLetterNum)
        let keywordLetterNum = keyword.charCodeAt(i) - 65
        console.log(keywordLetterNum)
        let encryptedLetter = (25 - messageLetterNum - keywordLetterNum) % 26
        encryptedLetter < 0 ? encryptedLetter + 26 : encryptedLetter
        encryptedMessage += String.fromCharCode((((25 - messageLetterNum - keywordLetterNum)) % 26) + 65)
    }

  return encryptedMessage;
}

function decrypt(encryptedMessage, keyword) {
  
}

const Home = () => {
  useEffect(() => {});

  const [message, setMessage] = useState("");
  const [key, setKey] = useState("");
  const [encryptedMessageOutput, setEncryptedMessageOutput] = useState("");
  const [encryptedMessage, setEncryptedMessage] = useState("");
  const [decryptedMessageOutput, setdecryptedMessageOutput] = useState("");

  const handleMessageChange = (e) => {
    setMessage(e.target.value);
  };

  const handleKeyChange = (e) => {
    setKey(e.target.value);
  };

  const handleFormEncryptSubmit = (e) => {
    e.preventDefault();

    setEncryptedMessageOutput(`Encrypted Message: ${encrypt(message, key)}`);
  };

  const handleEncryptedMessageChange = (e) => {
    setEncryptedMessage(e.target.value);
  };

  const handleFormDecryptSubmit = (e) => {
    e.preventDefault();

    setdecryptedMessageOutput(
      `Decrypted Message: ${decrypt(encryptedMessage, key)}`
    );
  };

  return (
    <div className="home">
      <div className="split left">
        <form onSubmit={handleFormEncryptSubmit}>
          <div>
            <label htmlFor="message">Message:</label>
            <input
              type="text"
              id="message"
              value={message}
              onChange={handleMessageChange}
            />
          </div>
          <div>
            <label htmlFor="key">Key:</label>
            <input
              type="text"
              id="key"
              value={key}
              onChange={handleKeyChange}
            />
          </div>
          <button type="submit">Submit</button>
        </form>
        <section className="output">
          <h3>{encryptedMessageOutput}</h3>
        </section>
      </div>
      <div className="split right">
        <form onSubmit={handleFormDecryptSubmit}>
          <div>
            <label htmlFor="encryptedMessage">Encrypted Message:</label>
            <input
              type="text"
              id="encryptedMessage"
              value={encryptedMessage}
              onChange={handleEncryptedMessageChange}
            />
          </div>
          <div>
            <label htmlFor="key">Key:</label>
            <input
              type="text"
              id="key"
              value={key}
              onChange={handleKeyChange}
            />
          </div>
          <button type="submit">Submit</button>
        </form>
        <section className="output">
          <h3>{decryptedMessageOutput}</h3>
        </section>
      </div>
    </div>
  );
};

export default Home;
