import React, { useEffect, useState } from "react";

function isAlphabeticOrSpace(text) {
  // Regular expression to match only alphabetical characters and spaces
  const regex = /^[A-Za-z\s]+$/;
  return regex.test(text);
}

function isAlphabeticOnly(text) {

  if (text === "") {

    return true
  }
  // Regular expression to match only alphabetical characters
  const regex = /^[A-Za-z]+$/;
  return regex.test(text);
}

function encryptOrDecrypt(message, keyword) {
  message = message.toUpperCase();
  keyword = keyword.toUpperCase();
  let encryptedMessage = "";

  for (let i = 0; i < message.length; i++) {
    if (message[i] === " ") {
      encryptedMessage += " ";
      continue;
    }

    let messageLetterNum = message.charCodeAt(i) - 65;
    let keywordLetterNum = keyword.charCodeAt(i % keyword.length) - 65;
    let encryptedLetter = 25 - messageLetterNum - keywordLetterNum;

    encryptedLetter < 0 ? (encryptedLetter += 26) : (encryptedLetter += 0);

    encryptedMessage += String.fromCharCode(encryptedLetter + 65);
  }

  return encryptedMessage;
}

const Home = () => {
  useEffect(() => {});

  const [message, setMessage] = useState("");
  const [key, setKey] = useState("");
  const [encryptedMessageOutput, setEncryptedMessageOutput] = useState("");
  const [encryptedMessage, setEncryptedMessage] = useState("");
  const [decryptedMessageOutput, setDecryptedMessageOutput] = useState("");
  const [keyError, setKeyError] = useState("");

  const handleMessageChange = (e) => {
    setMessage(e.target.value);
  };

  const handleKeyChange = (e) => {
    const keyInput = e.target.value;

    if (isAlphabeticOnly(keyInput)) {
      setKey(keyInput);
      setKeyError("");
    } else {
      setKeyError("Key must contain only alphabetical characters.");
    }
  };

  const handleFormEncryptSubmit = (e) => {
    e.preventDefault();
    if (key === "") {

      setKeyError("Need a key my G!")
    }
    if (!keyError) {
      setEncryptedMessageOutput(`${encryptOrDecrypt(message, key)}`);
    }
  };

  const handleEncryptedMessageChange = (e) => {
    setEncryptedMessage(e.target.value);
  };

  const handleFormDecryptSubmit = (e) => {
    e.preventDefault();
    if (!keyError) {
      setDecryptedMessageOutput(`${encryptOrDecrypt(encryptedMessage, key)}`);
    }
  };

  return (
    <div className="home">
      <h2 className="title">Encryption/Decryption</h2>
      <form onSubmit={handleFormEncryptSubmit}>
        <div className="form-group">
          <label htmlFor="message">Message:</label>
          <input
            type="text"
            id="message"
            value={message}
            onChange={handleMessageChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="key">Key:</label>
          <input
            type="text"
            id="key"
            value={key}
            onChange={handleKeyChange}
          />
          {keyError && <div className="error">{keyError}</div>}
        </div>
        <div className="form-footer">
          <button type="submit" className="submit-button">Submit</button>
          <div className="output">
            <h3>{encryptedMessageOutput}</h3>
          </div>
        </div>
      </form>
    </div>
  );  
};

export default Home;
