import React, { useState } from "react";

function isAlphabeticOrSpace(text) {
  const regex = /^[A-Za-z\s]+$/;
  return regex.test(text);
}

function isAlphabeticOnly(text) {
  if (text === "") {
    return true;
  }
  const regex = /^[A-Za-z]+$/;
  return regex.test(text);
}

function encryptOrDecrypt(message, keyword) {
  message = message.toUpperCase();
  keyword = keyword.toUpperCase();
  let encryptedMessage = "";

  for (let i = 0; i < message.length; i++) {
    if (message[i] === " ") {
      encryptedMessage += " ";
      continue;
    }

    let messageLetterNum = message.charCodeAt(i) - 65;
    let keywordLetterNum = keyword.charCodeAt(i % keyword.length) - 65;
    let encryptedLetter = 25 - messageLetterNum - keywordLetterNum;

    encryptedLetter < 0 ? (encryptedLetter += 26) : (encryptedLetter += 0);

    encryptedMessage += String.fromCharCode(encryptedLetter + 65);
  }

  return encryptedMessage;
}

const Home = () => {
  const [message, setMessage] = useState("");
  const [key, setKey] = useState("");
  const [encryptedMessageOutput, setEncryptedMessageOutput] = useState("");
  const [keyError, setKeyError] = useState("");
  const [charInput, setCharInput] = useState(""); // New state for character input

  const handleMessageChange = (e) => {
    setMessage(e.target.value);
  };

  const handleKeyChange = (e) => {
    const keyInput = e.target.value;

    if (isAlphabeticOnly(keyInput)) {
      setKey(keyInput);
      setKeyError("");
    } else {
      setKeyError("Key must contain only alphabetical characters.");
    }
  };

  const handleFormEncryptSubmit = (e) => {
    e.preventDefault();
    if (key === "") {
      setKeyError("Key must not be empty.");
      return;
    }
    if (!keyError) {
      setEncryptedMessageOutput(`${encryptOrDecrypt(message, key)}`);
    }
  };

  const handleCharInputChange = (e) => {
    const input = e.target.value.toUpperCase();
    if (input.length === 1 && isAlphabeticOnly(input)) {
      setCharInput(input);
    } else {
      setCharInput("");
    }
  };

  return (
    <div className="home">
      <div className="panel">
        <h2 className="title">Encryption/Decryption</h2>
        <form onSubmit={handleFormEncryptSubmit}>
          <div className="form-group">
            <label htmlFor="message">Message:</label>
            <input
              type="text"
              id="message"
              value={message}
              onChange={handleMessageChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="key">Key:</label>
            <input
              type="text"
              id="key"
              value={key}
              onChange={handleKeyChange}
            />
            {keyError && <div className="error">{keyError}</div>}
          </div>
          <div className="form-footer">
            <button type="submit" className="submit-button">
              Submit
            </button>
            <div className="output">
              <h3>{encryptedMessageOutput}</h3>
            </div>
          </div>
        </form>
      </div>
      <div className="wheel-container">
        <div className="wheel">
          <span style={{ "--i": 0 }}></span>
          <span style={{ "--i": 1 }}></span>
          <span style={{ "--i": 2 }}></span>
          <span style={{ "--i": 3 }}></span>
          <span style={{ "--i": 4 }}></span>
          <div className="letter">
            <b style={{ "--i": 0 }}>A</b>
            <b style={{ "--i": 1 }}>B</b>
            <b style={{ "--i": 2 }}>C</b>
            <b style={{ "--i": 3 }}>D</b>
            <b style={{ "--i": 4 }}>E</b>
            <b style={{ "--i": 5 }}>F</b>
            <b style={{ "--i": 6 }}>G</b>
            <b style={{ "--i": 7 }}>H</b>
          </div>
        </div>
        <div className="wheel inner" style={{ transform: `rotate(${(charInput.charCodeAt(0) - 65) * 45}deg)`}}>
          <span style={{ "--i": 0 }}></span>
          <span style={{ "--i": 1 }}></span>
          <span style={{ "--i": 2 }}></span>
          <span style={{ "--i": 3 }}></span>
          <span style={{ "--i": 4 }}></span>
          <div className="letter">
            <b style={{ "--i": 0 }}>A</b>
            <b style={{ "--i": 1 }}>B</b>
            <b style={{ "--i": 2 }}>C</b>
            <b style={{ "--i": 3 }}>D</b>
            <b style={{ "--i": 4 }}>E</b>
            <b style={{ "--i": 5 }}>F</b>
            <b style={{ "--i": 6 }}>G</b>
            <b style={{ "--i": 7 }}>H</b>
          </div>
        </div>
        <div className="wheel small">
          {/* Small inner circle */}
          <span style={{ "--i": 0 }}></span>
          <div>
            <input
              type="text"
              value={charInput}
              onChange={handleCharInputChange}
              maxLength={1}
              placeholder="A"
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
